steps = {};

function CreateStep(x, y, zone, target, description, completeEvent, questId, npcMessage)
  local step = {};
  step.x = x;
  step.y = y;
  step.zone = zone;
  step.description = description;
  step.target = target;
  step.completeEvent = completeEvent;
  step.questId = questId;
  step.npcMessage = npcMessage;
  return step;
end

